import React from 'react';
import s from './Login.module.css';
import {connect} from "react-redux";

const Login = (props) => {

    let loginRef = React.createRef();
    let paswordRef = React.createRef();
    let rememberMeRef = React.createRef();

    const login = () => {
        props.login && props.login(loginRef.current.value, paswordRef.current.value, rememberMeRef.current.checked)

    }

    return (
        <div className={s.loginPanel}>
            <div className={s.loginRegistration}>
                <button>Login</button>
                <button>Registration</button>
            </div>
            <div className={s.loginInputs}>
                Email
                <input type="email" placeholder="Email@"/>
                Passward
                <input type="password" placeholder="Password"/>
            </div>
            <div className={s.loginRegistration}>
                <input type="checkbox"/>
                <h5>Remember me</h5>
                <input type="button" value="Login" onClick={login}/>
            </div>
        </div>
    );
}
let mapStateToProps = () => ({
    isAuth: state.auth.isAuth,
    status: state.auth.status,
    message: state.auth.message,
    captchaUrl: state.auth.captchaUrl
});

export default connect()(Login)
