import React from 'react';
import s from './Dialogs.module.css';
import {NavLink} from "react-router-dom";

const DialogItem = (props) => {
    let path = '/dialogs/' + props.id;
    return (
        <div className={s.dialogs + ' ' + s.active}>
            <NavLink to={path}>{props.name}</NavLink>
        </div>
    )
}

const Message = (props) => {
    return (
        <div><img src={props.src}/>{props.message}</div>
    )
}

const Dialogs = (props) => {
    return (
        <div className={s.dialogs}>
            <div className={s.dialogItems}>
                <DialogItem name='Dimich' id='1'/>
                <DialogItem name='Andrey' id='2'/>
                <DialogItem name='Sveta' id='3'/>
            </div>

            <div className={s.mesages}>
                <Message message='Hi' src='https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'/>
                <Message message='how are you?' src='https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'/>
                <Message message='yo it-camasutra!' src='https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'/>


            </div>

        </div>
    );
}

export default Dialogs
