import React from "react";
import styles from "./Users.module.css";
import * as axios  from 'axios';
import userPhoto from "../../assets/"

let Users = props => {

  if (props.users.length === 0) {

    axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {
  
      props.setUsers(response.data.items
      
        // id: 1,
        // photoUrl:
        //   "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg/220px-Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg",
        // followed: false,
        // fullName: "Dmitry",
        // status: "I am a boss",
        // location: { city: "Minsk", country: "Belarus" }
      
     
    );
    })

    
  }
  return (
    <div>
      {props.users.map(u => (
        <div key={u.id}>
          <span>
            <div>
              <img src={u.photos.small !== null ? u.photos.small : ''} className={styles.userPhoto} alt="" />
            </div>
            <div>
              {u.followed ? (
                <button
                  onClick={() => {
                    props.unfollow(u.id);
                  }}
                >
                  Unfollow
                </button>
              ) : (
                <button
                  onClick={() => {
                    props.follow(u.id);
                  }}
                >
                  Follow
                </button>
              )}
            </div>
          </span>
          <span>
            <span>
              <div>{u.name}</div>
              
            </span>
            <span>
              <div>{u.status}</div>
              
            </span>
          </span>
        </div>
      ))}
    </div>
  );
};
export default Users;
