import React from "react";
import styles from "./Users.module.css";
import { setUsers } from "./../../Reducers-BLL/FriendsReducer";

let Users = props => {
        if (props.users.lenght)
  props.setUsers([
    {
      id: 1,
      photoUrl:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg/220px-Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg",
      followed: false,
      fullName: "Dmitry",
      status: "I am a boss",
      location: { city: "Minsk", country: "Belarus" }
    },
    {
      id: 2,
      photoUrl:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg/220px-Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg",
      followed: true,
      fullName: "Sasha",
      status: "I am a boss too",
      location: { city: "Moscow", country: "Russia" }
    },
    {
      id: 3,
      photoUrl:
        "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg/220px-Andrew_Lincoln_%2842749683025%29_%28cropped%29.jpg",
      followed: false,
      fullName: "Andrew",
      status: "I am a boss three",
      location: { city: "Kiev", country: "Uckraine" }
    }
  ]);

  return (
    <div>
      {props.users.map(u => (
        <div key={u.id}>
          <span>
            <div>
              <img src={u.photoUrl} className={styles.userPhoto} />
            </div>
            <div>
              {u.followed ? (
                <button
                  onClick={() => {
                    props.unfollow(u.id);
                  }}
                >
                  Unfollow
                </button>
              ) : (
                <button
                  onClick={() => {
                    props.follow(u.id);
                  }}
                >
                  Follow
                </button>
              )}
            </div>
          </span>
          <span>
            <span>
              <div>{u.fullName}</div>
              <div>{u.status}</div>
            </span>
            <span>
              <div>{u.location.country}</div>
              <div>{u.location.city}</div>
            </span>
          </span>
        </div>
      ))}
    </div>
  );
};
export default Users;
