import React from "react";
import userPhoto from "../../assets/images/user-search.png";
import styles from "./Users.module.css";
import { NavLink } from 'react-router-dom';
import * as axios from "axios";

let Users = (props) => {

  let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
  let pages = [];
  for (let i = 1; i <= pagesCount; i++) {
    pages.push(i);
  }

  return (
    <div>
      <div>
        {pages.map(p => {
          return (
            <span className={props.currentPage === p && styles.selectedPage} onClick={e => {
              props.onPageChanged(p);
            }} >
              {` ${p} `}
            </span>
          );
        })}
      </div>
      {props.users.map(u => (
        <div key={u.id}>
          <span>
            <div>
            <NavLink to={ '/profile/' + u.id}>
              <img
                src={u.photos.small !== null ? u.photos.small : userPhoto}
                className={styles.userPhoto}
                alt=""
              />
              </NavLink>
            </div>
            <span>
              <div>{u.name}</div>
            </span>
            <span>
              <div>{u.status ? u.status : "no status"}</div>
            </span>
            <span>
              <div>ID: {u.id}</div>
            </span>
            <div>
              {u.followed 
              ? (<button onClick={() => {

                props.unfollow(u.id); 
                
                
                }}> Unfollow</button>) 
                : (<button onClick={() => {

                  axios.get(`https://social-network.samuraijs.com/api/1.0/users?count=${
                    this.props.pageSize
                    }&page=${pageNumber}`
                  )
                    .then(response => {
                      this.props.toggleIsFetching(false)
                      this.props.setUsers(response.data.items);
                    });
                  props.follow(u.id);


                }}> Follow </button>)}
            </div>
          </span>
        </div>
      ))}
    </div>
  );
};

export default Users;
