import React from 'react';
import s from './MyPosts.module.css';
import Post from "./Post/Post";

const MyPosts = (props) => {

    return (

        <div className={s.notes}>
            <div>
                <textarea value={props.currentPostMessValue} onChange={(e) => {
                    debugger;
                    props.dispatch({
                        type: 'CHANGE_CURRENT_POST_MESSAGE',
                        postMess: e.currentTarget.value
                    })
                }} />dsdsds
                <div>
                    <button onClick={() => {
                        props.dispatch({
                            type: 'ADD_MESSAGE_POSTS',
                            message: props.currentPostMessValue
                        });
                    }
                    }>Отправить
                    </button>
                </div>
            </div>

            {
                props.posts.map((el, i) => {
                    return (
                        <Post key={i} urlAvaPhoto={el.urlAvaPhoto} message={el.message} likesCount={el.likesCount}/>
                    )
                })
            }
        </div>


    );
}

export default MyPosts
