import React from "react";
import styles from "./Users.module.css";
import * as axios  from 'axios';
import userPhoto from "../../assets/images/user-search.png"

class Users extends React.Component{
  constructor(props){
    
  }

  getUsers = () => {

    if (this.props.users.length === 0) {
      axios.get('https://social-network.samuraijs.com/api/1.0/users?count=100')
      .then(response => {
        this.props.setUsers(response.data.items)
      })
    }
  };

  render() {
    return <div>
      <button onClick= {this.getUsers}> Get users</button>
        {this.props.users.map(u => (
          <div key={u.id}>
            <span>
              <div>
                <img src={u.photos.small !== null ? u.photos.small : userPhoto} className={styles.userPhoto} alt="" />
              </div>
              <div>
                {u.followed ? (
                  <button
                    onClick={() => {
                      this.props.unfollow(u.id);
                    }}
                  >
                    Unfollow
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      this.props.follow(u.id);
                    }}
                  >
                    Follow
                  </button>
                )}
              </div>
            </span>
            <span>
              <span>
                <div>{u.name}</div>
                
              </span>
              <span>
                <div>{u.status}</div>
                
              </span>
            </span>
          </div>
        ))}
      </div>;
  }
}



export default Users;
