import React, {Component} from 'react';
import s from './Dialogs.module.css';
import {NavLink} from "react-router-dom";

const DialogItem = (props) => {
    let path = '/dialogs/' + props.id;
    return (
        <div className={s.dialogs + ' ' + s.active}>
            <NavLink to={path}>{props.name}</NavLink>
        </div>
    )
}

const Message = (props) => {
    return (
        <div><img src={props.src}/>{props.message}</div>
    )
}

const Dialogs = (props) => {
    const ref = React.createRef();
    return (
        <div className={s.dialogs}>
            <div className={s.dialogItems}>
                {
                    props.state.dialogsItem.map((el, i) => {
                        return (
                            <DialogItem key={i} name={el.name} id={el.id}/>
                        )
                    })
                }
            </div>
            <div className={s.mesages}>
                <div>

                    <textarea message={ref}></textarea>
                    <button onClick={() => {
                        props.addMessagePosts(message)
                    }}>Добавить сообщение</button>
                </div>
                {
                    props.state.messages.map((el, i) => {
                        return (
                            <Message key={i} message={el.message} src={el.foto}/>
                        )
                    })
                }
            </div>
        </div>
    );
}

export default Dialogs
