import React, {Component} from 'react';

// import './App.css';

const Dialogs = (props) => {
    let message = React.createRef();
    return <div>
        <div>
            <b>DIALOGS</b>

            <ul>
                {props.DialogPage.dialogs.map(d => <li>{d}</li>)}
            </ul>
        </div>
        <div>
            <b>Messages</b>
            <ul>
                {props.DialogPage.messages.map(m => <li>{m}</li>)}
            </ul>
            <textarea ref={message}></textarea>
            <button onClick={() => {
                props.addMessage(message.current.value);
                message.current.value = '';
            }
            }>
                Add
            </button>
        </div>
    </div>
}


export default Dialogs;
