import React from "react";
import userPhoto from "../../assets/images/user-search.png";
import styles from "./Users.module.css";
import { NavLink } from 'react-router-dom';
import instence from "../../DAL/axios-instance";

let Users = (props) => {

  let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
  let pages = [];
  for (let i = 1; i <= pagesCount; i++) {
    pages.push(i);
  }

  let onUsersFolowing= (u)=> {
    
    props.toggleFollowingProgress(true);

    instence.delete(`follow/${u.id}`)
      .then(response => {
        if (response.data.resultCode === 0) {
          props.unfollow(u.id);
        }
        props.toggleFollowingProgress(false);
      });
  }

  return (
    <div>
      <div>
        {pages.map(p => {
          return (
            <span className={props.currentPage === p && styles.selectedPage} onClick={e => {
              props.onPageChanged(p);
            }} >
              {` ${p} `}
            </span>
          );
        })}
      </div>
      {props.users.map(u => (
        <div key={u.id}>
          <span>
            <div>
              <NavLink to={'/profile/' + u.id}>
                <img
                  src={u.photos.small !== null ? u.photos.small : userPhoto}
                  className={styles.userPhoto}
                  alt=""
                />
              </NavLink>
            </div>
            <span>
              <div>{u.name}</div>
            </span>
            <span>
              <div>{u.status ? u.status : "no status"}</div>
            </span>
            <span>
              <div>ID: {u.id}</div>
            </span>
            <div>
              {u.followed
                ? (<button disabled={props.folowingInProgress} onClick={ ()=>{onUsersFolowing(u)} }> Unfollow</button>)

                : (<button disabled={props.folowingInProgress} onClick={() => {
                  props.toggleFollowingProgress(true);

                  instence.post(`follow/${u.id}`)
                    .then(response => {
                      if (response.data.resultCode == 0) {
                        props.follow(u.id);
                      }
                      props.toggleFollowingProgress(false);
                    });

                }}> Follow </button>)}
            </div>
          </span>
        </div>
      ))}
    </div>
  );
};

export default Users;
