import React from 'react';
import s from './Dialogs.module.css';
import {NavLink} from "react-router-dom";
import {connect} from "react-redux";
import mapStateToProps from "react-redux/es/connect/mapStateToProps";
import mapDispatchToProps from "react-redux/es/connect/mapDispatchToProps";

const DialogItem = (props) => {
    let path = '/dialogs/' + props.id;

    return (
        <div className={s.dialogs + ' ' + s.active}>
            <NavLink to={path}>{props.name}</NavLink>
        </div>
    )
}

const Message = (props) => {
    return (
        <div><img src={props.src} alt=''/>{props.message}</div>
    )
}

const Dialogs = (props) => {
de
    return (
        <div className={s.dialogs}>
            <div className={s.dialogItems}>
                {
                    props.dialogsItem.map((el, i) => {
                        return (
                            <DialogItem key={i} name={el.name} id={el.id}/>
                        )
                    })
                }
            </div>
            <div className={s.mesages}>
                <div>
                    <textarea value={props.currentDialogMessValue} onChange={(e) => {
                        props.dispatch({
                            type: 'CHANGE_CURRENT_DIALOG_MESSAGE',
                            dialMess: e.currentTarget.value
                        })
                    }}></textarea>
                    <button onClick={() => {
                        props.dialogMessages.message()
                    }}>Добавить сообщение
                    </button>
                </div>
                {
                    props.dialogMessages.map((el, i) => {
                        return (
                            <Message key={i} message={el.message} src={el.urlFoto}/>
                        )
                    })
                }
            </div>
        </div>
    );
}

