import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

let store = {
    _state: {
        currentValue: '',
        posts: [
            {
                id: 1,
                urlAvaPhoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg',
                message: 'Hi, how are you?',
                likesCount: '12'
            },
            {
                id: 2,
                urlAvaPhoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg',
                message: 'It\'s my first post',
                likesCount: '23'
            }
        ],
        profiles: {
            userName: 'Andruha',
            birthDay: '12.08.1984',
            city: 'Minsck',
            aducation: 'higher',
            webSite: 'drunya35@gmail.com'
        },
        dialogsItem: [
            {
                name: 'Dimich',
                id: '1'
            },
            {
                name: 'Andrey',
                id: '2'
            },
            {
                name: 'Sveta',
                id: '3'
            }
        ],
        dialogMessages: [
            {
                id: '1',
                message: 'Hi',
                urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
            },
            {
                id: '2',
                message: 'how are you?',
                urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
            },
            {
                id: '3',
                message: 'yo it-camasutra!',
                urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
            }
        ]

    },

    dispatch(action) {
        switch (action.type) {
            case 'ADD_MESSAGE_POSTS':
                this._state.posts.unshift({
                    message: action.message,
                    urlAvaPhoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg',
                    likesCount: 0,
                });
                this._state.currentValue = '';
                this._callbacks();
            case 'ADD_MESSAGE_DIALOGS':
                this._state.dialogMessages.unshift({
                    message: action.message,
                    urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
                });
                this._state.currentValue = '';
                this._callbacks();
            case 'CHANGE_CURRENT_VALUE':
                this._state.currentValue = action.a;
                this._callbacks();
            default:
                return this._state;
        }
    },

    getState() {
        return this._state;
    },

    _callbacks() {
        alert('Error');
    },

    subscribe(renderAll) {
        this._callbacks = renderAll;
    }
}

let renderAll = () => {
    ReactDOM.render(<App state={store.getState()} dispatch={store.dispatch.bind(store)}/>, document.getElementById('root'));
}
renderAll();

store.subscribe(() => {
    renderAll();
})




serviceWorker.unregister();
