import React from "react";
import styles from "./Users.module.css";
import * as axios from "axios";
import userPhoto from "../../assets/images/user-search.png";

class Users extends React.Component {
  componentDidMount() {
    axios
      .get(
        "https://social-network.samuraijs.com/api/1.0/users?count=10&page=27"
      )
      .then(response => {
        this.props.setUsers(response.data.items);
      });
  }

  render() {

    let pagesCount = this.props.totalUsersCount / this.props.pageSize;
    let pages = [];
    for (let i=1; i <=pagesCount; i++){
      pages.push(i);
    }

    return (
      <div>
        <div>
        {pages.map(p => {
          return <span className={this.props.currentPage === p && styles.selectedPage}>{p}</span>
        })}
          
          <span>2</span>
          <span className={styles.selectedPage}>3</span>
          <span>4</span>
          <span>5</span>
        </div>
        {this.props.users.map(u => (
          <div key={u.id}>
            <span>
              <div>
                <img
                  src={u.photos.small !== null ? u.photos.small : userPhoto}
                  className={styles.userPhoto}
                  alt=""
                />
              </div>
              <span>
                <div>{u.name}</div>
              </span>
              <span>
                <div>{u.status ? u.status : "no status"}</div>
              </span>
              <div>
                {u.followed ? (
                  <button
                    onClick={() => {
                      this.props.unfollow(u.id);
                    }}
                  >
                    Unfollow
                  </button>
                ) : (
                  <button
                    onClick={() => {
                      this.props.follow(u.id);
                    }}
                  >
                    Follow
                  </button>
                )}
              </div>
            </span>
          </div>
        ))}
      </div>
    );
  }
}

export default Users;
