import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

let store = {
    _state: {
        posts: [
            {
                id: 1,
                message: 'Hi, how are you?',
                likesCount: '12'
            },
            {
                id: 2,
                message: 'It\'s my first post',
                likesCount: '23'
            }
        ],
        profiles: {
            userName: 'Andruha',
            birthDay: '12.08.1984',
            city: 'Minsck',
            aducation: 'higher',
            webSite: 'drunya35@gmail.com'
        },
        dialogsItem: [
            {
                name: 'Dimich',
                id: '1'
            },
            {
                name: 'Andrey',
                id: '2'
            },
            {
                name: 'Sveta',
                id: '3'
            }
        ],
        dialogMessages: [
            {
                id: '1',
                message: 'Hi',
                urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
            },
            {
                id: '2',
                message: 'how are you?',
                urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
            },
            {
                id: '3',
                message: 'yo it-camasutra!',
                urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
            }
        ]

    },


    dispatch(action) {

    },

    getState() {
        return this._state;
    }
}
let state = store.getState();

let addMessagePosts = (message) => {
    state.posts.push({message, likesCount: 0});
    clearMessageArea(message);
    renderAll()
}
let addMessageDialogs = (message) => {
    state.dialogMessages.push({
        message: message,
        urlFoto: 'https://i.ytimg.com/vi/ToNcTEAEUqo/maxresdefault.jpg'
    });
    clearMessageArea(message);
    renderAll()
}
const clearMessageArea = (message) => {

    message = '';
}

let renderAll = () => {
    ReactDOM.render(<App state={store.getState()}
                         dispatch={store.dispatch.bind(store)}
                         addMessagePosts={addMessagePosts}
                         addMessageDialogs={addMessageDialogs}/>, document.getElementById('root'));
}
renderAll();
serviceWorker.unregister();
