const SET_IS_AUTH = 'App/AUTH/SET_IS_AUTH';

let initialState = {
    isAuth: false,
    userInfo: {
        userId: null,
        userName: null,
        avatarUrl: ''
    }
}

export const setIsAuth = (value) => ({type: SET_IS_AUTH, value});

const AuthReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_IS_AUTH: {
            return  {
                ...state,
                isAuth: action.value,
            }
        }

        default: {
            return state
        }
    }
};

export default AuthReducer
