import React from 'react';
import s from './MyPosts.module.css';
import Post from "./Post/Post";

const MyPosts = (props) => {

//     let posts = [
//         {
//             message: 'Hi, how are you?',
//             likesCount: '12'
//         },
//         {
//             message: 'It\'s my first post',
//             likesCount: '23'
//         }
//     ]
    const message = React.createRef();

    return (

        <div className={s.notes}>
            <div>
                <textarea value={props.currentValue} onChange={props.currentValue}></textarea>
                <div>
                    <button onClick={() => {
                        props.addMessagePosts(message.current.value)
                        message.current.value = '';
                    }
                    }>Отправить
                    </button>
                </div>
            </div>

            {
                props.state.posts.map((el, i) => {
                    return (
                        <Post key={i} urlAvaPhoto={el.urlAvaPhoto} message={el.message} likesCount={el.likesCount}/>
                    )
                })
            }
        </div>


    );
}

export default MyPosts
