import React from 'react';
import s from './Header.module.css';
import {NavLink} from "react-router-dom";
import {login} from "../../Reducers-BLL/LoginReducer";
import connect from "react-redux/es/connect/connect";
import {Login} from "../Login/Login";

const Header = (isAuth, userInfo) => {

    return (
        <div className={s.header}>
            {
                isAuth && <div>{userInfo.userName}
                    <img src='https://cdn.auth0.com/blog/react-redux/logo.png' alt=''/>
                </div>
            }
            {!isAuth && <div>
                <NavLink to='/login'>Sign In</NavLink>
            </div>
            }

        </div>

    );
}

let mapStateToProps = (state) => ({
  userInfo: state.auth.userInfo,
    
});
let mapDispatchToProps = (dispatch) => ({
    login: (email, password, rememberMe) => {
        dispatch(login(email, password, rememberMe))
    },
});

let LoginContainer = connect(mapStateToProps, mapDispatchToProps)(Login)

export default Header
